import styled from "styled-components";

const StyledPage = styled.div`
padding:0 1rem
 margin:0 auto;
  display: grid;
  grid-template-columns:1fr;
  grid-auto-flow:row;
  /* grid-row-gap:1rem; */
  text-align: center;
  width: 70vw;
  min-width: 30rem;
  
background-color: ${props => props.theme.pureWhite};
h4{
  margin:0 auto;
  margin-bottom:1rem ;
  padding:0.5rem 1rem;
  text-align: center;
  font-size: 1.6rem;
  font-weight: normal;
  box-shadow: ${props => props.theme.formBS};
  background:${props => props.theme.pureWhite};
width:100%;
  border-radius:0.5rem;
  color:${props => props.theme.blues[2]};
}
.Form {
    justify-items: center;
    align-items: center;
  }
  form {
    justify-items: center;
    align-items: center;
  }
`;

const MiniStyledPage = styled.div`
  display: grid;
  padding:0 1rem;
  margin:0 auto;
  grid-template-columns:1fr;
  grid-auto-flow:row;
  
  text-align: center;
  width: 50vw;
  min-width: 30rem;
  background-color: ${props => props.theme.pureWhite};
  
  h4{
    margin:0 auto;
  margin-bottom:1rem ;
  padding:0.5rem 1rem;
  text-align: center;
  font-size: 1.6rem;
  font-weight: normal;
  box-shadow: ${props => props.theme.formBS};
  background:${props => props.theme.pureWhite};
width:100%;
  border-radius:0.5rem;
  color:${props => props.theme.blues[2]};
}
  /* .Form {
    justify-items: center;
    align-items: center;
  }
  form {
    justify-items: center;
    align-items: center;
  } */
`;

const MinimStyledPage = styled.div`
  display: grid;
   margin:0 auto;
  
